      const matrix = channelOrder.map(key => {
        return processedItem[key] !== null ? processedItem[key] : 0;
      });

      // 构建 each_card
      const each_card = {
        matrix: matrix,
        model: processedItem.model,
        price_cny: processedItem.price_cny,
        id: processedItem.id
      };

